#define maxn 100010
const int mod = 998244353;
bool vis[maxn];
int phi[maxn], pr[maxn], prcnt;
inline int qpow(int base, int power)
{
	int ret = 1;
	for (; power; power >>= 1, base = 1ll * base * base % mod)
		power & 1 ? ret = 1ll * ret * base % mod : 0;
	return ret;
}
int pw[65538], pw1[65538];
inline int qqpow(int power)
{
	return 1ll * pw[power & 65535] * pw1[power >> 16] % mod;
}
